SET NAMES UTF8;
DROP DATABASE IF EXISTS fm;
CREATE DATABASE fm CHARSET=UTF8;
USE fm;
#轮播图表
CREATE TABLE fm_image(
    id TINYINT PRIMARY KEY AUTO_INCREMENT,
    img_url VARCHAR(255)
);

#电子书类别
USE fm;
CREATE TABLE fm_book_class(
    cid INT PRIMARY KEY AUTO_INCREMENT,
    cll VARCHAR(255)
);
#电子书信息表
USE fm;
CREATE TABLE fm_book(
    bid INT PRIMARY KEY AUTO_INCREMENT,
    cid INT,
    cll VARCHAR(255),
    title VARCHAR(255),
    label VARCHAR(255),
    point INT,
    img_url VARCHAR(255),
    parts INT,
    content VARCHAR(255)   
);






