#用户列表
USE fm;
CREATE TABLE fm_user(
    uid INT PRIMARY KEY AUTO_INCREMENT,
    uname VARCHAR(25) NOT NULL DEFAULT '',
    upwd VARCHAR(32) NOT NULL DEFAULT '',
    email VARCHAR(32) NOT NULL DEFAULT ''
);
#商城首页轮播
USE fm;
CREATE TABLE fm_index_swipe(
    index_id INT PRIMARY KEY AUTO_INCREMENT,
    img_url VARCHAR(255)
);

#商品列表
USE fm;
CREATE TABLE fm_product(
    pid INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(25),
    subtitle VARCHAR(255),
    price DECIMAL(10,2),
    img_url VARCHAR(255)
);


#商品详情轮播图
USE fm;
CREATE TABLE fm_swipe(
    swipe_id INT PRIMARY KEY AUTO_INCREMENT,
    pid INT ,
    img_url VARCHAR(255)
);










#购物车列表fm_shopCart
USE fm;
CREATE TABLE fm_shopCart(
    iid INT PRIMARY KEY AUTO_INCREMENT,
    pid INT,
    uid INT, 
    count INT,
    is_cheecked INT
);






